"use strict";function rotateHandle(e,n){e.style.transform="rotate("+n+"deg)"}function updateTime(){rotateHandle(handSeconds,seconds/60*360-90),seconds++,rotateHandle(handMinutes,(minutes+seconds/60)/60*360-90),rotateHandle(handHours,hours+hours/12*360-90)}var handSeconds=document.querySelector(".hand.seconds"),handMinutes=document.querySelector(".hand.minutes"),handHours=document.querySelector(".hand.hours"),currentDay=new Date,seconds=currentDay.getSeconds(),minutes=currentDay.getMinutes(),hours=currentDay.getHours();rotateHandle(handSeconds,seconds/60*360-90),rotateHandle(handMinutes,minutes/60*360-90),rotateHandle(handHours,hours/12*360-90),setInterval(updateTime,1e3);