"use strict";function rotateHandle(e,n){e.style.transform="rotate("+n+"deg)"}function updateTime(){seconds++,rotateHandle(handSeconds,seconds/60*360-90),rotateHandle(handMinutes,(minutes+seconds/60)/60*360-90),rotateHandle(handHours,(hours+minutes/60)/12*360-90)}var handSeconds=document.querySelector(".hand.seconds"),handMinutes=document.querySelector(".hand.minutes"),handHours=document.querySelector(".hand.hours"),currentDay=new Date,seconds=currentDay.getSeconds(),minutes=currentDay.getMinutes(),hours=currentDay.getHours(),degSeconds=seconds/60*360-90,degMinutes=(minutes+seconds/60)/60*360-90,degHours=(hours+minutes/60)/12*360-90;rotateHandle(handSeconds,degSeconds),rotateHandle(handMinutes,degMinutes),rotateHandle(handHours,degHours),setInterval(updateTime,1e3);