"use strict";function rotateHandle(e,t){e.style.transform="rotate("+t+"deg)"}function updateTime(){seconds++,rotateHandle(handSeconds,seconds/60*360-90),rotateHandle(handMinutes,(minutes+seconds/60)/60*360-90),rotateHandle(handHours,(hours+minutes/60)/12*360-90)}function startInterval(e){var t=setInterval(updateTime,e)}var handSeconds=document.querySelector(".hand.seconds"),handMinutes=document.querySelector(".hand.minutes"),handHours=document.querySelector(".hand.hours"),timeSlider=document.querySelector("#timeSlider"),secInterval=1e3,currentDay=new Date,seconds=currentDay.getSeconds(),minutes=currentDay.getMinutes(),hours=currentDay.getHours(),degSeconds=seconds/60*360-90,degMinutes=(minutes+seconds/60)/60*360-90,degHours=(hours+minutes/60)/12*360-90;rotateHandle(handSeconds,degSeconds),rotateHandle(handMinutes,degMinutes),rotateHandle(handHours,degHours);var interval=setInterval(updateTime,secInterval);timeSlider.addEventListener("change",function(e){if(console.log("slider",timeSlider.value),0===timeSlider.value){clearInterval(t);var t=setInterval(updateTime,secInterval)}else timeSlider.value>0?(clearInterval(interval),startInterval(1e3*timeSlider.value)):timeSlider.value<0&&(clearInterval(interval),startInterval(Math.abs(10*timeSlider.value/1e3)))});